mapper.update:
update inventorydemo.product_meta 

    name = #{name}
    vendor_name = #{vendor.name}
    default_uom = #{defaultUom}
    activity = #{activity}

where id = #{id}

update inventorydemo.product_meta set
activity = true activity = false
where id = #{id}

mapper.select:
select id from product_meta
where activity = 0 and id in

  #{productId}

mapper.insert:
create table if not exists inventorydemo.product_meta ( id varchar(20) not
   null, name varchar(50) not null, vendor_name varchar(20), default_uom varchar(8) not null
   default 'UNIT', activity boolean default false, primary key (id) );
